import React from 'react';
import './App.css';
import {Switch , Route , Link} from 'react-router-dom';
import {Homepage} from './pages/homepage/homepage.com';
import Shoppage from './pages/shop/shop.comp';
import Header from './components/header/header';
import Signinout from './pages/sign in out/sign-in-out';
import {auth, createUserProfiledoc} from './firebase/firebase.util';
class App extends React.Component {

  constructor(){
    super();
    this.state = {
      currentuser : null
    }
  }
  unsubscribefromAuth = null;

  componentDidMount(){
    this.unsubscribefromAuth = auth.onAuthStateChanged(async user =>{
      this.setState({currentuser : user})
      createUserProfiledoc(user);
      console.log(user)
    })
  }
  componentWillUnmount(){
    this.unsubscribefromAuth();
  }
  render(){
      return (
    <div className="App">
      <Header currentuser={this.state.currentuser}/>
      <Switch>
        <Route exact path='/' component={Homepage}/>
        <Route exact  path='/shop' component={Shoppage}/>
        <Route exact path='/login' component={Signinout}/>
      </Switch>

    </div>
  );
  }

}

export default App;
