import React from "react";
import './cart-dropd.style.scss';
import {connect} from 'react-redux';
import CustomButton from "../custom-button/custom-button.com";
import CartItem from '../cart-item/cart-item.comp';
import {selectCartItems} from '../../redux/cart/ccart.selectors';
import {createStructuredSelector} from 'reselect';
import {withRouter} from 'react-router-dom'
import {toggleCartHidden} from '../../redux/cart/cart.action';


const cartDropd = ({cartItems, history, dispatch})=>(
    <div className="cart-dropdown">
        <div className="cart-items">
           {
               cartItems.length
               ?( cartItems.map(cartItem=> ( <CartItem key={cartItem.id} item={cartItem} /> )))
               : (<span className="empty-message">Your Cart Is Empty</span>)
           } 
        </div>
        <CustomButton onClick={()=>{
            history.push('/checkout')
            dispatch(toggleCartHidden)
            }}> GO TO CHECKOUT </CustomButton>
    </div>
)

const mapStateToProps = createStructuredSelector({
    cartItems :selectCartItems
})


export default withRouter(connect(mapStateToProps)(cartDropd));
